import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../components/Home.css';

const AdminSignUp = () => {
  const [form, setForm] = useState({
    adminId: '',
    name: '',
    email: '',
    phone: '',
    password: ''
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.adminId || !form.name || !form.email || !form.phone || !form.password) {
      setError('All fields are required.');
      return;
    }
    try {
      const res = await fetch('http://localhost:5000/api/admin/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });
      const data = await res.json();
      if (res.ok) {
        navigate('/admin/login');
      } else {
        setError(data.message || 'Sign up failed.');
      }
    } catch (err) {
      setError('Server error.');
    }
  };

  return (
    <div className="auth-container">
      <h2>Admin Sign-Up</h2>
      <form onSubmit={handleSubmit} className="auth-form">
        <input type="text" name="adminId" placeholder="Admin ID" value={form.adminId} onChange={handleChange} />
        <input type="text" name="name" placeholder="Name" value={form.name} onChange={handleChange} />
        <input type="email" name="email" placeholder="Email" value={form.email} onChange={handleChange} />
        <input type="text" name="phone" placeholder="Phone" value={form.phone} onChange={handleChange} />
        <input type="password" name="password" placeholder="Password" value={form.password} onChange={handleChange} />
        {error && <div className="error">{error}</div>}
        <button type="submit">Sign Up</button>
        <button type="button" onClick={() => navigate('/admin/login')}>Already have an account? Sign In</button>
      </form>
    </div>
  );
};

export default AdminSignUp;
